import React, { useState } from 'react'
import './styles.css'




const Add = () => {


    //handle innputs
    const [title,setTitle]=useState('');
    const [body,setBody]=useState('');

    const submitForm = (e) =>
    {
        e.preventDefault();
        fetch("http://localhost:5000/addPosts",
        {
          method:"post",
          body:JSON.stringify
          ({

            title,
            body

          })

        })
        .then(res=>res.json())
        .then(res=>
          {
            console.log(res);
          })
    }

    return (
      <>
      <h1 className="title">Share your story with us!</h1>
        <div className="form-container">
            <div className="mb-3">
            <label for="exampleFormControlInput1" className="form-label" className="labels">Title</label>
            <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Enter the title"
            onChange={(e)=>
              {
                 e.preventDefault();
                 setTitle(e.target.value)
              }}/>
          </div>
          <div className="mb-3">
            <label for="exampleFormControlTextarea1" className="form-label" className="labels">Post Body</label>
            <textarea className="form-control" id="exampleFormControlTextarea1" rows="6"
            placeholder="Share your story.."
            onChange={(e)=>
            {
               e.preventDefault();
               setBody(e.target.value)
            }}></textarea>
          </div>
          <button type="button" class="btn btn-outline" id="submit"
          onSubmit={()=>submitForm()}>
          Submit</button>
        </div>
      </>
    )
}

export default Add
